package org.wcong.test.leetcode.string;

import org.junit.Assert;
import org.junit.Test;
import org.wcong.test.algorithm.leetcode.string.PalindromePartitioning2;

/**
 * @author wcong<wc19920415@gmail.com>
 * @since 07/06/2017
 */
public class PalindromePartitioning2Test {

    @Test
    public void testMinCutBruteForceDp() {
        PalindromePartitioning2 solution = new PalindromePartitioning2();
        Assert.assertTrue(solution.minCutDp("aab") == 1);
        Assert.assertTrue(solution.minCutDp("daa") == 1);
        Assert.assertTrue(solution.minCutDp("") == 1);
    }

}
